// Generated by LiveScript 1.3.1
document.addEventListener('scroll', function(){
  return Array.from(document.querySelectorAll(".img")).map(function(it){
    var box;
    box = it.getBoundingClientRect();
    if (box.y >= 0 && box.y + box.height <= window.innerHeight) {
      return it.style.background = "url(" + it.getAttribute('data-src') + ")";
    } else {
      return it.style.background = '#f00';
    }
  });
});